#serveur UDP (CTRL+F2 pour stopper)
import socket

UDP_IP_DU_BINOME = "127.0.0.1"   #adresse de mon serveur
UDP_PORT_DU_BINOME = 5000        #port de mon serveur

# Cr√©e un socket UDP
sock = socket.socket(socket.AF_INET, socket.SOCK_DGRAM)
message = '--------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------' 
octets = message.encode("Utf8")
print(octets)
sock.sendto(octets, (UDP_IP_DU_BINOME, UDP_PORT_DU_BINOME))
